mapper.update:
UPDATE approval_doc
SET status = #{status}
WHERE approval_doc_id = #{docId}
  AND status = 'DRAFT'

UPDATE approval_line
SET status = #{status}
WHERE approval_doc_id = #{docId}

UPDATE approval_line l
    JOIN (
    SELECT approval_doc_id, MIN(order_num) AS min_order
    FROM approval_line
    WHERE approval_doc_id = #{docId}
    GROUP BY approval_doc_id
    ) x
ON x.approval_doc_id = l.approval_doc_id
    AND x.min_order = l.order_num
    SET l.status = #{toStatus}
WHERE l.approval_doc_id = #{docId}
  AND l.status = #{fromStatus}

mapper.select:
SELECT COUNT(*)
FROM approval_line
WHERE approval_doc_id = #{docId}
  AND approver_id = #{userId}
  AND status = 'WAITING'

SELECT order_num
FROM approval_line
WHERE approval_doc_id = #{docId}
  AND user_id = #{userId}
  AND status = 'WAITING'
    LIMIT 1

mapper.update:
UPDATE approval_line
SET status = 'APPROVED',
    approved_at = NOW()
WHERE approval_doc_id = #{docId}
  AND user_id = #{userId}
  AND status = 'WAITING'

mapper.select:
SELECT COUNT(*)
FROM approval_line
WHERE approval_doc_id = #{docId}
  AND order_num = #{orderNum}
  AND status = 'WAITING'

mapper.update:
UPDATE approval_line
SET status = 'WAITING'
WHERE approval_doc_id = #{docId}
  AND order_num = #{orderNum}
  AND status = 'PENDING'

UPDATE approval_doc
SET status = #{status}
WHERE approval_doc_id = #{docId}
