mapper.update:
UPDATE schedule
SET status = #{status}
WHERE schedule_id = #{scheduleId}

mapper.delete:
DELETE FROM schedule
WHERE schedule_id IN

    #{id}

mapper.update:
UPDATE schedule
SET git_url = #{gitUrl}
WHERE schedule_id = #{scheduleId}

UPDATE schedule
SET git_url = NULL
WHERE schedule_id = #{scheduleId}

UPDATE schedule
SET file_storage_url = #{fileStorageUrl}
WHERE schedule_id = #{scheduleId}

UPDATE schedule
SET file_storage_url = NULL
WHERE schedule_id = #{scheduleId}
