mapper.select:
SELECT
    mt.task_id AS taskId,
    mt.task_description AS taskDescription,
    mt.personal_status AS personalStatus
FROM my_task mt
WHERE mt.schedule_id = #{scheduleId}
    AND mt.user_id = #{userId}
    AND mt.personal_status = #{status}
ORDER BY mt.task_id DESC

SELECT COUNT(*)
FROM my_task mt
WHERE mt.schedule_id = #{scheduleId}
    AND mt.user_id = #{userId}
    AND mt.personal_status = #{status}

mapper.update:
UPDATE my_task
SET personal_status = #{personalStatus}
WHERE task_id = #{taskId}
AND schedule_id = #{scheduleId}
AND user_id = #{userId}
